trait Counter {
    fn count(&self, input: &str) -> i32;
}

struct SimpleParenthesesCounter;
struct SimplePositionCounter;

impl Counter for SimpleParenthesesCounter {
    fn count(&self, input: &str) -> i32 {
        let mut count: i32 = 0;
        for c in input.chars() {
            if c.eq_ignore_ascii_case(&'(') {
                count += 1;
            }
            if c.eq_ignore_ascii_case(&')') {
                count -= 1;
            }
        }
        count
    }
}

impl Counter for SimplePositionCounter {
    fn count(&self, input: &str) -> i32 {
        let mut position: i32 = 0;
        let mut count: i32 = 0;
        for c in input.chars() {
            if c.eq_ignore_ascii_case(&'(') {
                count += 1;
            }
            if c.eq_ignore_ascii_case(&')') {
                count -= 1;
            }
            position += 1;
            if count.is_negative() {
                return position;
            }
        }
        position
    }
}

