trait ParenthesesCounter {
    fn count_parentheses(&self, input: &str) -> i32;
}

struct SimpleParenthesesCounter;

impl ParenthesesCounter for SimpleParenthesesCounter {
    fn count_parentheses(&self, input: &str) -> i32 {
        let mut count = 0;
        for c in input.chars() {
            if c.eq_ignore_ascii_case(&'(') {
                count += 1;
            }
            if c.eq_ignore_ascii_case(&')') {
                count -= 1;
            }
        }
        count
    }
}

